			//$count[1] = "0";
			$inarray = 0;
			for ($y=0;$y<sizeof($table_array);$y++) {

				if (!in_array($table_array[$y], $unique)) {
					// Found a unique entry....
					$unique[$inarray] = $table_array[$y];
					$inarray++;
				}
			}
			for ($y=0;$y<sizeof($table_array);$y++) {
				for ($z=0;$z<$inarray;$z++ ) {
						if ($unique[$z]==$table_array[$y]) {
							$count[$z] = $count[$z] + 1;
