function fillout($var,$len, $nofill=false) {
		if (strlen($var) <= $len && !$nofill) {
			while (strlen($var)<$len) {
					$var = $var . " ";
			}
			if ($var=="____0") {
				$var="_____";
			}
		} elseif (strlen($var) >= $len) {
			$var = substr($var, 0, ($len-3)) . "...";
		}
	return $var;
}
function filloutnum($var,$len) {
		while (strlen($var)<$len) {
				$var = "0" . $var;
		}
	return $var;
}
