	$pdf->Open();
	$pdf->AliasNbPages();
	$pdf->AddPage();
	$pdf->SetFont('Times','',12);
	$pdf->SetFont('Arial','',8);
}
function StartPrintPDF() {
	global $pdf;
	$pdf=new PDF_AutoPrint();
	$pdf->Open();
	$pdf->AliasNbPages();
	$pdf->AddPage();
	$pdf->SetFont('Times','',12);
	$pdf->SetFont('Arial','',8);
	$pdf->AutoPrint(false);
}
