			if (strtoupper($GLOBALS['PDF-ExtraFieldsInTable'])=="YES") {
				$pdf->SetFont('Arial','',6);
				$pdf->FancyTable2col($header,$data);
			}
			unset($header);
			unset($data);
			unset($num);
			unset($list);
		}


		$pdf->Ln(8);

		line();

}

