function ReturnRepositorySwitcher($post_to,$post_to_parent="") {
	global $host,$pass,$database,$table_prefix,$user,$swrepos,$EnableRepositorySwitcher,$lang,$postto, $session;
	ob_start();
	ShowRepositorySwitcher($post_to,$post_to_parent="");
	$ret = ob_get_contents();
	ob_end_clean();
	return($ret);
}
