function ImportSettings() {
	global $legend;
	MustBeAdmin();

	qlog(INFO, "Importing users");
	$sarr = array();
	if ($_FILES['userfile']['tmp_name'] && $_FILES['userfile']['name']) {
		$fp=fopen($_FILES['userfile']['tmp_name'] ,"rb");
		$filecontent=fread($fp,filesize($_FILES['userfile']['tmp_name'] ));
