			$sql = "INSERT INTO " . $GLOBALS['TBL_PREFIX'] . "settings(setting,value,discription) VALUES (";
			foreach ($set_row AS $element) {
				$sql .= "'" . mres(base64_decode($element)) . "',";
				$element_count++;
			}
			$sql .= ")";
			$sql = str_replace(",)",")",$sql);
