						$cf .= " style='width: " . $extrafield['limitddtowidth'] . "px;' onblur=\"SetWidth(" . $extrafield['limitddtowidth'] . ", this);\" onmouseover=\"SetWidthDelayed('auto', this);this.focus();\" onmouseout='StopCount();'";
