function RemoveTableRowFromTemplateByTag($template, $tag_to_remove) {

	$t1 = explode($tag_to_remove, $template);
	$cnt = count($t1);
	$result = "";

	for ($i=0;$i<$cnt;$i++) {
		if ($i < $cnt-1) {
			$t2 = preg_split("/\<tr/i", $t1[$i]);
			$t1[$i] = "";
			for ($y=0;$y<(count($t2)-1);$y++) {
				if ($y>0) {
					$t1[$i] .= "<tr";
				}
				$t1[$i] .= $t2[$y];
			}
		}
		if ($i > 0) {
			$t2 = preg_split("/\<\/tr>/i", $t1[$i]);
			$t1[$i] = "";
			for ($y=1;$y<count($t2);$y++) {
				if ($y>1) {
					$t1[$i] .= "</tr>";
				}
				$t1[$i] .= $t2[$y];
			}
		}
		$result .= $t1[$i];
	}
	return($result);
}


