function CleanupExtractedAscii($in) {
	if ($in == "{{locked/error}}") {
		return($in);
	}
	// some information comes in html decoded
	$out = html_entity_decode(strtolower($in));

	// strip html & xml tags
	$out = strip_tags($out);

	// strip all leestekens (replace with a space to be sure!)
	$out = str_replace(",", " ", $out);
	$out = str_replace(":", " ", $out);
	$out = str_replace(";", " ", $out);
	$out = str_replace(".", " ", $out);
	$out = str_replace("!", " ", $out);
	$out = str_replace("?", " ", $out);
	$out = str_replace(")", " ", $out);
	$out = str_replace("(", " ", $out);
	$out = str_replace("'", " ", $out);
	$out = str_replace("\"", " ", $out);
	$out = str_replace("`", " ", $out);
	$out = str_replace("/", " ", $out);
	$out = str_replace("]", " ", $out);
	$out = str_replace("[", " ", $out);
	$out = str_replace("{", " ", $out);
	$out = str_replace("}", " ", $out);

	// strip all non-ascii chars
	$out = preg_replace('/[^(\x20-\x7F)]*/','', $out);

	$tmp = explode(" ", $out);
	$cnt = sizeof($tmp);
	$result = array();
	for ($i=0;$i<$cnt;$i++) {
		if (strlen($tmp[$i]) < 25 && $tmp[$i] != "" && strlen($tmp[$i]) > 4 && !in_array($tmp[$i], $result)) {
			$result[] = $tmp[$i];
		}
		// save some memory
		$tmp[$i] = "";
	}
	$out = implode(" ", $result);
	return($out);
}
function SetExtractedAsciiData($fileid, $data) {
	
